const updateCartItemQuantity = async (req, res) => {
  const cartItemId = req.body.cartItemId;
  const quantity = parseInt(req.body.quantity, 10); // Parse quantity as integer
  let discount = null;
  let subtotal = parseFloat(req.body.subtotal); // Parse subtotal as float
  const price = parseFloat(req.body.price); // Parse price as float
  const code = req.body.code;

  try {
    // Update the cart item quantity
    const cart = await carts.findOne({ ref: cartItemId });
    const oldQuantity = cart.quantity;
    cart.quantity = quantity;
    await cart.save();

    const coupon = await coupons.findOne({ code: code, isListed: true });
    if (coupon) {
      discount = coupon.discount;
    }

    const oldPrice = parseFloat(price * oldQuantity); // Calculate old price as float

    // Calculate new price and subtotal
    const newPrice = parseFloat(price * quantity);
    subtotal = parseFloat(subtotal - oldPrice + newPrice); // Update subtotal with old and new price
    console.log(subtotal);

    if (discount) {
      // Calculate new discounted subtotal
      const newDiscountedSubtotal = parseFloat((subtotal * (1 - discount / 100)).toFixed(2));

      // Send response with discount
      res.status(200).send({
        msg: 'Cart item quantity updated successfully',
        quantity: quantity,
        discount: discount,
        newDiscountedSubtotal: newDiscountedSubtotal,
        cartItemId: cartItemId,
        total: newDiscountedSubtotal,
      });
    } else {
      // Send response without discount
      res.status(200).send({
        msg: 'Cart item quantity updated successfully',
        quantity: quantity,
        subtotal: subtotal,
        cartItemId: cartItemId,
        total: subtotal,
      });
    }
  } catch (err) {
    // Send an error response
    res.status(500).send({ msg: 'Failed to update cart item quantity', error: err });
  }
};

